INSERT INTO `article`(`id`, `created_user_id`, `title`, `content`, `reading_amount`, `praise_clicks`, `deleted`, `original`, `original_author_id`, `create_time`, `update_time`) VALUES ('310e61afd80c4116b1bfc6aa8178fc83', '2', 'vue使用阿里图标的方法', '::: hljs-center\n\n**vue中使用阿里的图标**\n\n:::\n需要导入如下的代码\n```@font-face {font-family: \"iconfont\";\n  src: url(\'iconfont.eot?t=1553860555623\'); /* IE9 */\n  src: url(\'iconfont.eot?t=1553860555623#iefix\') format(\'embedded-opentype\'), /* IE6-IE8 */\n  url(\'data:application/x-font-woff2;charset=utf-8;base64,\') format(\'woff2\'),\n  url(\'iconfont.woff?t=1553860555623\') format(\'woff\'),\n  url(\'iconfont.ttf?t=1553860555623\') format(\'truetype\'), /* chrome, firefox, opera, Safari, Android, iOS 4.2+ */\n  url(\'iconfont.svg?t=1553860555623#iconfont\') format(\'svg\'); /* iOS 4.1- */\n}\n\n.iconfont {\n  font-family: \"iconfont\" !important;\n  font-size: 16px;\n  font-style: normal;\n  -webkit-font-smoothing: antialiased;\n  -moz-osx-font-smoothing: grayscale;\n}\n\n\n```\n这个是每次使用一个阿里的图标都要导入他下面的iconfont的@font-face的代码\n\n记录2019.3.29', 0, 0, 0, 1, NULL, '2019-03-29 21:21:04', '2019-03-29 21:21:04');
